import React from 'react';
import PokemonCard from './PokemonCard';

class Button extends React.Component {
  render() {
    return (
      <button onClick={this.props.name}>
        {this.props.label}
      </button>
    )
  }
}

class Pokedex extends React.Component {
  constructor(props) {
    super(props);
    this.state = ({ pokeName: '' });
  }

  nextPokemon = event => {
    this.setState({ pokeName: event.target.value })
  }

  render() {
    const pokemons = this.props.pokemons;
    return (
      <div>
        <div className="pokedex-items">
          {
            pokemons
              .map((pokemon, i) => <PokemonCard key={i} pokemon={this.nextPokemon(pokemon)} />)
          }
        </div>
        <div>
          <Button handleClick={this.nextPokemon} label="Próximo Pokémon" />
        </div>
      </div>

    )
  }
}

export default Pokedex;
