//     return (
//       <button onClick={this.props.name}>
//         {this.props.label}
//       </button>
//     )
//   }
// }

class Pokedex extends React.Component {
  constructor(props) {
    super(props);
    this.state = ({ pokeName: '' });
  }

  nextPokemon = () => {
    this.setState({ pokeName: this.props.name })
  }

  render() {
    const pokemons = this.props.pokemons;
    return (
      <div>
        <div className="pokedex-items">
          {
            pokemons
              .filter(({name}) => [name, ''].includes(this.state.pokeName))
              .map((pokemon, i) => <PokemonCard key={i} pokemon={pokemon} />)
          }
        </div>
        {/* <div>
          <Button handleClick={this.nextPokemon} label="Próximo Pokémon" />
        </div> */}
      </div>

    )
  }
}

export default Pokedex;
