import React from 'react';
import PokemonCard from './PokemonCard';

class Pokedex extends React.Component {
  constructor(props) {
    super(props);
    this.state = { id: 25, index: 0 };
  }

  setNewState() {
    const newIndex = this.state.index + 1
    this.setState({ id: this.state.id, index: newIndex });
    console.log(this.state)
  }

  render() {
    const pokemons = this.props.pokemons;
    return (
      <div>
        <div className="pokedex-items">
          {
            pokemons
              .filter((index) => [index, '0'].includes(pokemons[index]))
              .map((pokemon, i) => <PokemonCard key={i} pokemon={pokemon} />)
          }
        </div>
        <div>
          <button onClick={this.setNewState}>
            Próximo Pokémon
          </button>
        </div>
      </div>
    )
  }
}

export default Pokedex;
