import React, { Component } from 'react';

class BoxList extends Component {
  constructor(props) {
    super(props);

    this.state = {
      str: 'a',
      open: false,
    }
  }

  changeStr(letter) {
    this.setState({ str: letter })
  }

  renderList(letters) {
    return (
      letters.map(letter =>
        <li onClick={() => this.changeStr(letter)}>
          {letter}
        </li>
      )
    );
  }

  render() {
    const letters = ['a', 'b', 'c', 'd'];
    return (
      <div>
        <div>
          {this.state.str}
        </div>
        <div>
          {this.renderList(letters)}
        </div>
      </div>
    )
  }
}

export default BoxList;
