import React, { Component } from 'react';

class Doguitchos extends Component {
  constructor(props) {
    super(props);

    this.state = {
      url: null,
      newDog: null,
    };
  }

  newFetch() {
    fetch('https://dog.ceo/api/breeds/image/random')
      .then(response => response.json())
      .then(data => this.setState({ url: data.message }));
  }

  componentDidMount() {
    newFetch();
  }

  addDog() {
    this.setState({ newDog: <div>
      <div>
        <input type="text" />
      </div>
      <div>
        <img src={this.state.url} alt="cachorro" />
      </div>
    </div> })
  }

  render() {
    return (
      <div>
        <div>
          <button type="button" onClick={this.addDog}>
            Novo dog!
          </button>
          {this.state.newDog}
        </div>
      </div>
    );
  }
}

export default Doguitchos;
